graphql.query:
mutation m {
    wrapper(
        first: 1
        last: 10
        wrapper: {
            customer: [
                {
                    customerBankingRelationship: {
                        contractKey: "{{ContractKey}}"
                        customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey}}"
                        customerKey: "{{CustomerKey}}"
                        contract: {
                            amount: 31
                            contractKey: "{{ContractKey}}"
                            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey}}"
                        }
                    }
                }
                {
                    customerBankingRelationship: {
                        contractKey: "{{ContractKey2}}"
                        customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey2}}"
                        customerKey: "{{CustomerKey2}}"
                        contract: {
                            amount: 32
                            contractKey: "{{ContractKey2}}"
                            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey2}}"
                        }
                    }
                }
                {
                    customerBankingRelationship: {
                        contractKey: "{{ContractKey3}}"
                        customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey3}}"
                        customerKey: "{{CustomerKey3}}"
                        contract: {
                            amount: 33
                            contractKey: "{{ContractKey3}}"
                            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey3}}"
                        }
                    }
                }
                {
                    customerBankingRelationship: {
                        contractKey: "{{ContractKey4}}"
                        customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey4}}"
                        customerKey: "{{CustomerKey4}}"
                        contract: {
                            amount: 34
                            contractKey: "{{ContractKey4}}"
                            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey4}}"
                        }
                    }
                }
            ]
        }
        where: {
            customerBankingRelationship: {
                some: {
                    customerBankingRelationshipKey: {
                        in: [
                            "{{CustomerBankingRelationshipKey}}"
                            "{{CustomerBankingRelationshipKey2}}"
                            "{{CustomerBankingRelationshipKey3}}"
                            "{{CustomerBankingRelationshipKey4}}"
                        ]
                    }
                }
            }
        }        
        order: { customerType: DESC, firstNaming: DESC }
    ) {
        totalCount
        edges {
            node {
                customerBankingRelationship {
                    contractKey
                    customerBankingRelationshipKey
                    customerId
                    customerKey
                    contract {
                        amount
                        contractKey
                        accountKey
                        customerBankingRelationshipId
                        contractType
                    }
                }
            }
        }
    }
}

mutation m {
  wrapper(
    first: 1
    last: 10
    wrapper: {
      customer: [
        {
          customerKey: "{{CustomerKey}}"
          contactPoint: [
            {
              contactPointKey: "{{ContactPointKey_a}}"
              customerKey: "{{CustomerKey}}"
            }
            {
              contactPointKey: "{{ContactPointKey_b}}"
              customerKey: "{{CustomerKey}}"
            }
          ]
          firstNaming: "{{FirstName}}"
          lastNaming: "{{LastNaming}}"
          customerType: PERSON
          customerBankingRelationship: {
            contractKey: "{{ContractKey}}"
            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey}}"
            customerKey: "{{CustomerKey}}"
          }
        }
        {
          customerKey: "{{CustomerKey2}}"
          contactPoint: [
            {
              contactPointKey: "{{ContactPointKey2_a}}"
              customerKey: "{{CustomerKey2}}"
            }
            {
              contactPointKey: "{{ContactPointKey2_b}}"
              customerKey: "{{CustomerKey2}}"
            }
          ]
          firstNaming: "{{FirstName2}}"
          lastNaming: "{{LastNaming2}}"
          customerType: ORGANISATION
          customerBankingRelationship: {
            contractKey: "{{ContractKey2}}"
            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey2}}"
            customerKey: "{{CustomerKey2}}"
          }
        }
        {
          customerKey: "{{CustomerKey3}}"
          contactPoint: [
            {
              contactPointKey: "{{ContactPointKey3_a}}"
              customerKey: "{{CustomerKey3}}"
            }
            {
              contactPointKey: "{{ContactPointKey3_b}}"
              customerKey: "{{CustomerKey3}}"
            }
          ]
          firstNaming: "{{FirstName3}}"
          lastNaming: "{{LastNaming3}}"
          customerType: PERSON
          customerBankingRelationship: {
            contractKey: "{{ContractKey3}}"
            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey3}}"
            customerKey: "{{CustomerKey3}}"
          }
        }
        {
          customerKey: "{{CustomerKey4}}"
          contactPoint: [
            {
              contactPointKey: "{{ContactPointKey4_a}}"
              customerKey: "{{CustomerKey4}}"
            }
            {
              contactPointKey: "{{ContactPointKey4_b}}"
              customerKey: "{{CustomerKey4}}"
            }
          ]
          firstNaming: "{{FirstName4}}"
          lastNaming: "{{LastNaming4}}"
          customerType: ORGANISATION
          customerBankingRelationship: {
            contractKey: "{{ContractKey4}}"
            customerBankingRelationshipKey: "{{CustomerBankingRelationshipKey4}}"
            customerKey: "{{CustomerKey4}}"
          }
        }
      ]
    }
    order: { customerType: DESC }
    where: {
      customerKey: {
        in: [
          "{{CustomerKey}}"
          "{{CustomerKey2}}"
          "{{CustomerKey3}}"
          "{{CustomerKey4}}"
        ]
      }
    }
  ) {
    edges {
      node {
        fullNaming
        lastNaming
        customerBankingRelationship {
          contractKey
          customerBankingRelationshipKey
          customerKey
        }
        contactPoint {
          contactPointKey
          contactPointValue
          contactPointType
        }
        customerType
        customerKey
        firstNaming
      }
    }
    totalCount
  }
}
